const inputArray = $input.first().json.content;

// Join array elements into one string (with spaces between)
const input = inputArray.join(" ");

// Replace newlines followed by optional spaces with a single space
// Collapse multiple spaces into one
const cleaned = input
  .replace(/\n\s*/g, " ")   // merge wrapped lines into a single line
  .replace(/\s{2,}/g, " ")  // collapse double spaces
  .trim();

return [{ json: { cleaned } }];

const inputArray = $input.first().json.content;

// Join all array elements into one string (with spaces between)
const input = inputArray.join(" ");

// Clean the string
const cleaned = input
  .replace(/\n\s*/g, " ")   // remove line breaks and surrounding spaces
  .replace(/\s{2,}/g, " ")  // collapse multiple spaces
  .trim();

// Return as a single field
return [{ json: { cleaned } }];

const url = $input.first().json.loc;

// Extract part between /articles/ and the next /
const match = url.match(/\/articles\/([^/]+)/);

let category = null;

if (match) {
  category = match[1]; // e.g. "carbon-free-buildings"

  // Convert to Title Case (remove "-" and capitalize each word)
  category = category
    .split("-")
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");
}

return [{ json: { category } }];

let raw = $input.first().json.content[0];

// Clean up invalid characters (newlines etc.)
raw = raw.replace(/[\n\r\t]/g, ' ');

// Parse JSON
const parsed = JSON.parse(raw);

// Replace <p> and </p> with newlines
if (parsed.props?.pageProps?.article?.body) {
  parsed.props.pageProps.article.body = parsed.props.pageProps.article.body
    .replace(/<p>/g, '\n')
    .replace(/<\/p>/g, '\n')
    .replace(/&nbsp;/g, ' ')  // optional: decode non-breaking spaces
    .trim();
}

return [{
  json: parsed
}];

// Get all items coming into this node
const items = $input.all();

// Slice the array to only the last 20
const last20 = items.slice(-20);

return last20;
